package com.test;

public class Switchcontrol {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		switch (i) {
		case 10:
			System.out.println("Case 10!");
			break;
		case 20:
			System.out.println("Case 20!");
			break;
		//case default:
		default:
			System.out.println("default Case");
			break;
		}
	}

}
