package com.tution;

public class ConcatDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="Abdul";
		String s2="Kalam";
		String r1,r2;
		System.out.println(s1);
		System.out.println(s2);
		
		r1=s1.concat(s2);
		System.out.println(r1);
		
		r2=s1+s2;
		System.out.println(r2);
		
	}

}
