package com.tution;

public class SubStringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Java is Kool";
		String r1,r2,r3;
		System.out.println(s);
		
		r1=s.substring(0);
		System.out.println(r1);
		
		r2=s.substring(5);
		System.out.println(r2);
		
		r3=s.substring(0, 3);
		System.out.println(r3);
	}

}
